
import * as population from "./country-by-population.json";
import * as c_flag from "./country-by-flag.json";
  
export function s (s) {
    return(s ? s.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ' ') : 0);
}

export function flag (n) {
    return(c_flag.default.filter(
        e => n === e.country
      )[0].flag_base64);
}

export function getPop ( attribute, value) {
  for (var i = 0; i < population.default.length; i++) {
    if (population.default[i][attribute] === value) {
      return population.default[i].z * 1000;
    }
  }
  return null;
}

export function chart_d (d) {
    var months = [
        "Jan",
        "Feb",
        "Mar",
        "Apr",
        "May",
        "Jun",
        "Jul",
        "Aug",
        "Sep",
        "Oct",
        "Nov",
        "Dec"
    ];
    return(new Date(d).getDate() + " " + months[new Date(d).getMonth()]);
}

export function toDate(timestamp){

  var date = new Date(timestamp);

  var day = date.getDate();

  var month = date.getMonth()+1;

  var year =  date.getFullYear().toString().substr(-2);

  return( month + '/' + day + '/' + year)
}