
import * as c_flag from "./country-by-flag.json";

export function s (s) {
    return(s.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ' '));
}
export function flag (n) {
    return(c_flag.default.filter(
        e => n === e.country
      )[0].flag_base64);
}
export function chart_d (d) {
    var months = [
        "Jan",
        "Feb",
        "Mar",
        "Apr",
        "May",
        "Jun",
        "Jul",
        "Aug",
        "Sep",
        "Oct",
        "Nov",
        "Dec"
    ];
    return(new Date(d).getDate() + " " + months[new Date(d).getMonth()]);
}